function generateWorkFlow(data, cb) {
    if (data.is_1st_entry === 'Y') {
        if (data.reopen === '1') {
            updateWFCResultForReopen(data, function (errX, successX, resultsX) {
                if (errX || !successX) {
                    logData(errX);
                    cb(errX, null);
                } else {
                    insetWFCResult(data, function (errI, successI, resultsI) {
                        if (errI || !successI) {
                            logData(errI);
                            cb(errI, null);
                        } else {
                            cb(null, true, resultsI);
                        }
                    })
                }
            })
        } else {
            insetWFCResult(data, function (errI, successI, resultsI) {
                if (errI || !successI || resultsI.insertId == 0) {
                    logData(errI);
                    cb(errI, null);
                } else {
                    cb(null, true, resultsI);
                }
            })
        }
    } else {
        var dataU = {
            "id": data.PKFIELDVAL
        };
        updateWFCResultForDispatch(dataU, function (errU, successU, resultsU) {
            if (errU || !successU) {
                logData(errU);
                cb(errU, null);
            } else {
                if (data.reopen === '1') {
                    updateWFCResultForReopen(data, function (errX, successX, resultsX) {
                        if (errX || !successX) {
                            logData(errX);
                            cb(errX, null);
                        } else {
                            insetWFCResult(data, function (errI, successI, resultsI) {
                                if (errI || !successI) {
                                    logData(errI);
                                    cb(errI, null);
                                } else {
                                    cb(null, true, resultsI);
                                }
                            })
                        }
                    })
                } else {
                    isWFCResultForGroupForUsrRly(data, function (errIU, successIU, resultsIU) {
                        if (errIU || !successIU) {
                            logData(errIU);
                            cb(errIU, null);
                        } else {
                            if (resultsIU == 1) {
                                data.user_id = null;
                            }
                            insetWFCResult(data, function (errI, successI, resultsI) {
                                if (errI || !successI) {
                                    logData(errI);
                                    cb(errI, null);
                                } else {
                                    cb(null, true, resultsI);
                                }
                            })
                        }
                    })

                }
            }
        });
    }
}

